mapper.insert:
insert
into
	market_board (
		boardnum
		, memberid
		, title
		, contents
		, category
	)
values (
		market_board_seq.nextval
		, #{memberid}
		, #{title}
		, #{contents}
		, #{category}
	)

mapper.select:
select boardnum , category , title from market_board where soldout = 'N'
and category = #{category}
order by boardnum desc
