mapper.insert:
insert
into
	market_board (
		boardnum
		, memberid
		, title
		, contents
		, category
	)
values (
		market_board_seq.nextval
		, #{memberid}
		, #{title}
		, #{contents}
		, #{category}
	)

insert
into
    market_reply (
        replynum
        , boardnum
        , memberid
        , replytext
    )
values (
        market_reply_seq.nextval
        , #{boardnum}
        , #{memberid}
        , #{replytext}
)
